import { useParams, Link, useHistory } from 'react-router-dom';

function Survey() {
	const { questionNumber } = useParams();
	const history = useHistory();
	const questionBack = parseInt(questionNumber) - 1;
	const questionNext = parseInt(questionNumber) + 1;

	const handleNextClick = () => {
		if (questionNumber === '10') {
			history.push('/results');
		} else {
			history.push(`/survey/${questionNext}`);
		}
	};

	return (
		<div>
			<h1>Questionnaire 🧮</h1>
			<h2>Question {questionNumber}</h2>
			{questionNumber !== '1' && <Link to={`/survey/${questionBack}`}>précédent</Link>}
			{questionNumber !== '10' ? <Link to={`/survey/${questionNext}`}>suivant</Link> : <Link to="/results">Résultats</Link>}
			<button onClick={handleNextClick}>Suivant</button>
		</div>
	);
}

export default Survey;
